mapper.update:
update Teacher
set name = #{name,jdbcType=VARCHAR},
  age = #{age,jdbcType=INTEGER},
  sex = #{sex,jdbcType=VARCHAR}
  major= #{major,jdbcType=VARCHAR}
  department = #{department,jdbcType=VARCHAR}
  college= #{college,jdbcType=VARCHAR}
  birth = #{birth,jdbcType=VARCHAR}
  salary = #{salary,jdbcType=VARCHAR}
  phone = #{phone,jdbcType=VARCHAR}
  email = #{email,jdbcType=VARCHAR}
  address = #{address,jdbcType=VARCHAR}
  ethnic= #{ethnic,jdbcType=VARCHAR}
  nativePlace = #{nativePlace,jdbcType=VARCHAR}
  certificate = #{certificate,jdbcType=VARCHAR}
  diploma = #{diploma,jdbcType=VARCHAR}
  schoolOfGraduation = #{schoolOfGraduation,jdbcType=VARCHAR}
  timeOfGraduation = #{timeOfGraduation,jdbcType=VARCHAR}
  positions = #{positions,jdbcType=VARCHAR}
  course = #{course,jdbcType=VARCHAR}
  clazz = #{clazz,jdbcType=VARCHAR}
where uid = #{uid,jdbcType=BIGINT}

update Teacher
set file = #{file,jdbcType=VARCHAR}
where uid = #{uid,jdbcType=BIGINT}
